import {useEffect, useState} from 'react';
import Todoitem from './Todoitem';


const Todolist=()=>{

    const [starttime_,setSDate]=useState();
    const [endtime_,setEDate]=useState();
    const [content_,setContent]=useState();
   
    const [items,setItem]=useState([
        {start_time:"12:00",end_time:"20:00",content:"Go Library"}
    
    ]);

    const mapping=items.map(item=>{
        
        return(
            <Todoitem item={item} key = {item.start_time}>

            </Todoitem>
        );
    });

    useEffect(()=>{
        console.log('render');

    });
 
    const add=(e)=>{
        e.preventDefault();
        setItem([
            ...items,{
            start_time: starttime_,
            end_time: endtime_,
            content:content,
        }])
    };

    return(
        <div >
            
            <form add={add}>
                <input type="text" placeholder="start time" value={starttime_}
                onChange={e=>setSDate(e.target.value)}>
                </input><br></br>
                <input type="text" placeholder="end time" value={endtime_}
                onChange={e=>setEDate(e.target.value)}>
                </input><br></br>
                <input type="text"  placeholder="content" value={content_}
                onChange={e=>setContent(e.target.value)}>
                </input><br></br>
                
                <button type="submit">Add</button>
                
            </form>
                {mapping}
        </div>
    );
}

export default Todolist;
